INSERT INTO `zy_expert` VALUES ('14', '褚健', '中国·澳门', '3', '1', '20150330_p1427686616.jpg', '注射美容 注射抗皱 注射精雕美容', '西婵整形美容医院特聘医师 \r<br><br><br><br><br><br><br>自然微整形与注射美容医师 \r<br><br><br><br><br><br><br>澳门皮肤美容学会秘书长顾问 ', '注射隆鼻、注射丰苹果肌、注射丰下巴、太阳穴填充、注射瘦脸等面部五官雕琢及脸型塑造；抬头纹、眉间纹、鱼尾纹等脸部皱纹等面部除皱技术；', '<p class=\"MsoNormal\">\r\n	<span style=\"line-height:1.5;\"> </span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<strong><span style=\"font-size:16px;\">| Doctor’s card</span></strong><br />\r\n<strong><span style=\"font-size:16px;\"> 褚健 &nbsp;医生名片</span></strong> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<strong><br />\r\n</strong> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-align:center;\">\r\n	<strong><img src=\"/Uploads/editor/image/20161111/20161111175434_57991.jpg\" alt=\"\" /><br />\r\n</strong> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<strong><br />\r\n</strong> \r\n</p>\r\n<div style=\"text-align:left;\">\r\n	<span style=\"line-height:1.5;font-size:14px;\"><strong>The honor and title</strong></span> \r\n</div>\r\n<div style=\"text-align:left;\">\r\n	<span style=\"line-height:1.5;font-size:14px;\"><strong>荣誉头衔</strong></span> \r\n</div>\r\n<div style=\"text-align:center;\">\r\n	<br />\r\n</div>\r\n<span style=\"font-size:14px;\">●西婵整形美容医院特聘医师&nbsp;</span><br />\r\n<span style=\"font-size:14px;\"> ●自然微整形与注射美容医师&nbsp;</span><br />\r\n<span style=\"font-size:14px;\"> ●澳门皮肤美容学会秘书长&nbsp;</span><br />\r\n<br />\r\n<strong><span style=\"font-size:16px;\">| Field of expertise</span></strong><br />\r\n<strong><span style=\"font-size:16px;\"> 擅长领域</span></strong><br />\r\n<br />\r\n<span style=\"font-size:14px;\"> <strong>· 面部五官雕琢及脸型塑造</strong><strong> ·</strong></span><br />\r\n<span style=\"font-size:14px;\"> 注射隆鼻、注射丰苹果肌、注射丰下巴、太阳穴填充、注射瘦脸等；</span><br />\r\n<br />\r\n<span style=\"font-size:14px;\"> <strong>· 面部除皱</strong><strong> ·</strong></span><br />\r\n<span style=\"font-size:14px;\"> 抬头纹、眉间纹、鱼尾纹等脸部皱纹。</span><br />\r\n<br />\r\n<strong><span style=\"font-size:14px;\">| Individual resum