INSERT INTO `zy_expert` VALUES ('71', '李斌', '中国', '2', '1', '20170320_p1490003491.jpg', '自体脂肪填充、吸脂塑型、假体隆胸、乳房矫正、鼻眼整形，双博士，健康保健，抗衰老管理', '<br><br><br><br><br><br><br><br>北京大学医学部临床医学博士M.D.（整形外科学）\r<br><br><br><br><br><br><br><br><br><br><br><br><br>瑞士日内瓦大学医学-理学博士M.D.-Ph.D.\r<br><br><br><br><br><br><br><br><br><br><br><br><br>瑞士日内瓦大学医学研究中心（C.M.U.）博士后\r<br><br><br><br><br><br><br><br><br><br><br><br><br>法国蒙彼利埃大学蒙彼利埃神经研究所（I.N.M.）博士后\r<br><br><br><br><br><br><br><br><br><br><br><br><br>中国中西医结合学会整形与美容分会青年委员会副主任委员\r<br><br><br><br><br><br><br><br><br><br><br><br><br>中国整形博士联盟会员', '自体脂肪填充以及脸部轮廓整形\r\n<br>吸脂塑型、健康保健与抗衰老管理\r\n<br>假体隆胸、乳房下垂矫正等乳房整形\r\n<br>重睑术、开眼角等常规眼科整形\r\n<br>假体隆鼻、耳软骨鼻尖塑形等鼻部整形\r\n', '<p style=\"text-align:center;\">\r\n	<br />\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<strong><span style=\"font-size:24px;color:#DFC5A4;\">SHESAYS西婵</span></strong><br />\r\n<strong><span style=\"font-size:24px;color:#DFC5A4;\">整形美容外科 | 副主任医师</span></strong> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<br />\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<img src=\"/Uploads/editor/image/20170320/20170320180425_72465.jpg\" alt=\"\" /> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"font-size:18px;color:#DFC5A4;\"><strong><br />\r\n</strong></span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"font-size:18px;color:#DFC5A4;\"><strong>李 斌（双博士后）</strong></span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"font-size:18px;color:#DFC5A4;\"><strong><br />\r\n</strong></span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<br />\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"color:#DFC5A4;\"><span style=\"color:#999999;font-size:16px;background-color:#FFFFFF;\"><br />\r\n</span></span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"color:#DFC5A4;\"><span style=\"color:#999999;background-color:#FFFFFF;\"><strong><span style=\"color:#DFC5A4;font-size:18px;\">▏Field of expertise</span></strong><br />\r\n<strong><span style=\"color:#DFC5A4;font-size:18px;\">擅长领域</span></strong><br />\r\n</span></span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"color:#DFC5A4;\"><span style=\"color:#999999;font-size:16px;background-color:#FFFFFF;\"><br />\r\n</span></span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"color:#DFC5A4;\"><span style=\"color:#999999;font-size:16px;background-color:#FFFFFF;\"><strong>· 各类整形美容外科手术 ·</strong><br />\r\n</span></span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<br />\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"color:#DFC5A4;\"><span style=\"color:#999999;font-size:16px;background-color:#FFFFFF;\"><strong><span style=\"font-size:18px;color:#DFC5A4;\">▏Personal profile</span></strong><br />\r\n<strong><span style=\"font-size:18px;color:#DFC5A4;\">医师介绍</span></strong><br />\r\n<br />\r\n</span></span> \r\n</p>\r\n<p style=\"text-align:left;\">\r\n	<span style=\"color:#DFC5A4;\"><span style=\"color:#999999;font-size:16px;background-color:#FFFFFF;\"> &nbsp; &nbsp; &nbsp;&nbsp;李斌博士，西婵整形外科副主任医师。早年毕业于华西医科大学（卫生部亚欧重点医科院校）六年制医学英语班，获得医学学士学位；在北京卫生部直属的三级甲等医院整形外科工作十余年，其间不脱离临床工作，先后通过在职申请学位方式获得中国协和医科大学研究生院硕士和北京大学医学部整形外科临床医学博士（M.D.）学位；后公派欧洲留学，获得瑞士日内瓦大学医学-理学博士(M.D.-Ph.D.)之后，又进行了两个博士后研究经历：瑞士日内瓦大学医学研究中心（C.M.U.）博士后以及法国蒙彼利埃大学蒙彼利埃神经研究所（I.N.M.）博士后。他通过对细胞代谢和抗衰老方面的深入研究，与国外整形外科同行交流学习临床手术经验，受益匪浅，成为我国整形界罕见的既有国内原装进口学府的医学博士学位，又有欧洲医学-理学博士学位的海归医学双博士。<br />\r\n<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp;李斌医生从事医学美容临床工作20余年，曾在北京大学第三临床教学医院（原“北医三院”）整形外科作住院总医师，进行临床强化培训工作，拥有夯实的整形外科临床技术。<br />\r\n<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp;在海内外医学期刊、网站上发表过学术论文20余篇。拥有丰富的医学美容理论知识、精细的外科手术操作技巧以及精细的整形外科手术操作手法，可为求美者提供严谨、专业、舒适、安心的求美体验。<br />\r\n</span></span> \r\n</p>\r\n<p style=\"text-align:left;\">\r\n	<span style=\"color:#DFC5A4;\"><span style=\"color:#999999;font-size:16px;background-color:#FFFFFF;\"><br />\r\n</span></span> \r\n</p>\r\n<p style=\"text-align:left;\">\r\n	<span style=\"color:#DFC5A4;\"><span style=\"color:#999999;font-size:16px;background-color:#FFFFFF;\"><br />\r\n</span></span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"color:#DFC5A4;\"><span style=\"color:#999999;background-color:#FFFFFF;\"><span style=\"color:#DFC5A4;font-size:18px;\"><strong></strong></span><span style=\"color:#DFC5A4;font-size:18px;\"><strong>▏Learn the infinite</strong></span><br />\r\n<span style=\"color:#DFC5A4;font-size:18px;\"><strong>业精于专，学而无涯</strong></span></span></span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"color:#DFC5A4;\"><span style=\"color:#999999;background-color:#FFFFFF;\"><span style=\"color:#DFC5A4;font-size:16px;\">锐思进取，不断超越自我</span><span style=\"font-size:16px;\"></span></span></span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"color:#DFC5A4;\"><span style=\"color:#999999;background-color:#FFFFFF;\"><span style=\"font-size:16px;\"><br />\r\n</span></span></span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"color:#DFC5A4;\"><span style=\"color:#999999;background-color:#FFFFFF;\"><span style=\"font-size:16px;\"><img src=\"/Uploads/editor/image/20170320/20170320173707_24622.jpg\" alt=\"\" /><br />\r\n</span></span></span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"color:#DFC5A4;\"><span style=\"color:#999999;background-color:#FFFFFF;\"><span style=\"font-size:16px;\"><br />\r\n</span></span></span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"color:#DFC5A4;\"><span style=\"color:#999999;background-color:#FFFFFF;\"><span style=\"font-size:16px;\"><img src=\"/Public/kindeditor/php/../../../Uploads/editor/image/20170320/20170320173722_51053.jpg\" alt=\"\" /><br />\r\n</span></span></span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<br />\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"color:#DFC5A4;\"><span style=\"color:#999999;background-color:#FFFFFF;\"><span style=\"font-size:16px;\">李斌医生多次出席国内外相关领域学术交流会议</span></span></span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<br />\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<br />\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"color:#DFC5A4;\"><span style=\"color:#999999;background-color:#FFFFFF;\"><span style=\"font-size:16px;\"><br />\r\n</span></span></span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"color:#DFC5A4;\"><span style=\"color:#999999;background-color:#FFFFFF;\"><span style=\"font-size:16px;\"><img src=\"/Uploads/editor/image/20170320/20170320173908_80136.jpg\" alt=\"\" /><br />\r\n</span></span></span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<br />\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<br />\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"color:#DFC5A4;\"><span style=\"color:#999999;background-color:#FFFFFF;\"><span style=\"font-size:16px;\">李斌医生曾赴海外深造学习并获瑞士、法国双博士后<br />\r\n</span></span></span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"color:#DFC5A4;\"><span style=\"color:#999999;background-color:#FFFFFF;\"><span style=\"font-size:16px;\"><br />\r\n</span></span></span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<br />\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<br />\r\n</p>', '8867', '1490002801', '2', '0', '长期坐诊', '西婵泛亚店', '0', '西婵整形美容外科副主任医师', '1', '25934', '2665', '', '李斌：西婵整形外科副主任医师，专业整形外科医师', '');
INSERT INTO `zy_expert` VALUES ('70', '黄琪平', '中国', '3', '1', '20161112_p1478937535.jpg', '西婵整形，西婵，医务中心，医疗服务，职业道德，医疗质量建设', '中国整形美容协会常务理事、医疗美容机构等级评审医生\r<br><br><br>四川医院管理协会常务理事\r<br><br><br>四川美容整形协会副秘书长\r<br><br><br>成都市高新区医学会副会长', '能够熟练掌握并严格执行国家医疗卫生管理法律、行政法规、部门规章和诊疗护理规范，时刻注重西婵院内的医疗质量建设，恪守医疗服务职业道德，把医疗质量和医疗轻松作为医院建设的重要内涵，做到了依法严谨执业，为爱美人士提供优质、轻松的医疗服务。', '<span style=\"font-size:16px;\"><strong>▏Business card</strong></span><br />\r\n<span style=\"font-size:16px;\"><strong>黄琪平 资料名片</strong></span><br />\r\n<br />\r\n<div style=\"text-align:center;\">\r\n	<span style=\"line-height:1.5;\"><img src=\"/Uploads/editor/image/20161112/20161112155651_96215.jpg\" alt=\"\" /></span> \r\n</div>\r\n<br />\r\n<span style=\"font-size:14px;\"><strong>The honor and title</strong></span><br />\r\n<span style=\"font-size:14px;\"><strong>荣誉头衔</strong></span><br />\r\n<br />\r\n<span style=\"font-size:14px;\">● 西婵连锁医务中心总监、副主任医师，中共党员</span><br />\r\n<span style=\"font-size:14px;\">● 中国整形美容协会常务理事、医疗美容机构等级评审医生</span><br />\r\n<span style=\"font-size:14px;\">● 四川医院管理协会常务理事</span><br />\r\n<span style=\"font-size:14px;\">● 四川美容整形协会副秘书长</span><br />\r\n<span style=\"font-size:14px;\">● 成都市高新区医学会副会长</span><br />\r\n<br />\r\n<br />\r\n<span style=\"font-size:16px;\"><strong>▏Personal profile</strong></span><br />\r\n<span style=\"font-size:16px;\"><strong>个人简介</strong></span><br />\r\n<br />\r\n<span style=\"font-size:14px;\">&nbsp; &nbsp; 黄琪平，西婵连锁医务中心总监、副主任医师，中共党员。毕业于第三军医大学军医系，从医二十余年，曾先后在军队、地方担任军队、卫训队长、门诊部主任、医务科科长以及社区卫生服务中心主任等职务。也曾多次在军队中立功受奖，与此同时，也曾在国内外多个大型期刊、网络等平台发表过医学论文数篇，深受医学行业专业领导、干部以及医学从业者的尊敬与认可！</span><br />\r\n<span style=\"font-size:14px;\">&nbsp; &nbsp; 黄总监自2005年加入西婵至今，担任过医务中心科长及副主任医师等职务，目前任医务中心总监一职。凭借恪尽职守、严谨务实的工作态度，友善温和、热情亲切的为人处事风格，让他在从事医学美容行业工作的多年中，能够熟练掌握并严格执行国家医疗卫生管理法律、行政法规、部门规章和诊疗护理规范，时刻注重西婵院内的医疗质量建设，恪守医疗服务职业道德，把医疗质量和医疗轻松作为医院建设的重要内涵，做到了依法严谨执业，为爱美人士提供优质、轻松的医疗服务。</span><br />\r\n<span style=\"font-size:14px;\">黄总监也被中国整形美容协会聘请为医疗美容机构等级评审医生</span><br />\r\n<br />\r\n<span style=\"font-size:16px;\"><strong>▏Industry expertise</strong></span><br />\r\n<span style=\"font-size:16px;\"><strong>业精于专，严谨执业</strong></span><br />\r\n<span style=\"font-size:14px;\">在前进的道路上不断学习，把医疗质量和医疗轻松作为医院建设的重要内涵</span><br />\r\n<br />\r\n<div style=\"text-align:center;\">\r\n	<span style=\"line-height:1.5;\"><img src=\"/Uploads/editor/image/20161112/20161112155756_58260.jpg\" alt=\"\" /></span> \r\n</div>\r\n<div style=\"text-align:center;\">\r\n	<span style=\"font-size:14px;line-height:1.5;\">黄总监多次参加国家卫计委委托中国整形美容协会</span> \r\n</div>\r\n<span style=\"font-size:14px;\"> \r\n<div style=\"text-align:center;\">\r\n	<span style=\"line-height:1.5;\">对江苏、广东两省的多家医疗美容机构的5A评审考核</span> \r\n</div>\r\n</span><span style=\"font-size:14px;\"> \r\n<div style=\"text-align:center;\">\r\n	<span style=\"line-height:1.5;\">同时也代表西婵院方接受中整协等上级领导对我院的内部评审等工作</span> \r\n</div>\r\n</span> \r\n<div style=\"text-align:center;\">\r\n	<br />\r\n</div>\r\n<div style=\"text-align:center;\">\r\n	<img src=\"/Uploads/editor/image/20161112/20161112155811_38561.jpg\" alt=\"\" /><br />\r\n</div>\r\n<span style=\"font-size:14px;\"> \r\n<div style=\"text-align:center;\">\r\n	<span style=\"line-height:1.5;\">黄总监始终秉承自律、诚信、服务的态度从事医美工作</span> \r\n</div>\r\n</span><span style=\"font-size:14px;\"> \r\n<div style=\"text-align:center;\">\r\n	<span style=\"line-height:1.5;\">在前进的道路上不断学习，依法严谨执业</span> \r\n</div>\r\n</span><br />\r\n<br />\r\n<span style=\"font-size:16px;\"><strong>▏His life</strong></span><br />\r\n<span style=\"font-size:16px;\"><strong>生活中的他</strong></span><br />\r\n<span style=\"font-size:14px;\">为人正直，友善温和，热情亲切</span><br />\r\n<br />\r\n<div style=\"text-align:center;\">\r\n	<img src=\"/Uploads/editor/image/20161112/20161112155836_65810.jpg\" alt=\"\" /><br />\r\n</div>\r\n<span style=\"font-size:14px;\"> \r\n<div style=\"text-align:center;\">\r\n	<span style=\"line-height:1.5;\">工作之余的黄总监待人亲切温和，喜欢旅游</span> \r\n</div>\r\n</span>', '1821', '1478937535', '23', '0', '长期', '长期', '0', '西婵连锁医务中心总监、副主任医师', '0', '39